var col11 = $('.col-11');
var col9 = $('.col-9');
var col8 = $('.col-8');
var col7 = $('.col-7');
var col6 = $('.col-6');
var col5 = $('.col-5');
var col4 = $('.col-4');
var col3 = $('.col-3');
var col2 = $('.col-2');
var col1 = $('.col-1');

function setNewCols(cols, colsFormat, colsFormat2) {
    for(col of cols) {
        if(col.classList.contains(colsFormat)) {
            col.classList.remove(colsFormat);
            col.classList.add(colsFormat2);
        }
    }
}

function handleResize() {
    var width = window.innerWidth;
    if(width <= 800) {
        setNewCols(col11, 'col-11', 'col-12');
        setNewCols(col9, 'col-9', 'col-12');
        setNewCols(col8, 'col-8', 'col-12');
        setNewCols(col7, 'col-7', 'col-12');
        setNewCols(col6, 'col-6', 'col-12');
        setNewCols(col5, 'col-5', 'col-12');
        setNewCols(col4, 'col-4', 'col-12');
        setNewCols(col3, 'col-3', 'col-12');
        setNewCols(col2, 'col-2', 'col-12');
        setNewCols(col1, 'col-1', 'col-12');
    } else {
        setNewCols(col11, 'col-12', 'col-11');
        setNewCols(col9, 'col-12', 'col-9');
        setNewCols(col8, 'col-12', 'col-8');
        setNewCols(col7, 'col-12', 'col-7');
        setNewCols(col6, 'col-12', 'col-6');
        setNewCols(col4, 'col-12', 'col-4');
        setNewCols(col3, 'col-12', 'col-3');
        setNewCols(col2, 'col-6', 'col-2');
        setNewCols(col1, 'col-6', 'col-1');
    }
}

// Agregar un event listener para el evento 'resize'
window.addEventListener("resize", handleResize);

// Llamada inicial para obtener la resoluciÃ³n actual de la ventana
handleResize();