$(document).ready(function () {
    // Agregar el evento change al campo de entrada de archivos
    $("#file-input").change(function () {
        var isValidExtension = checkFileExtension(this);
        if (!isValidExtension) {
            // Ocultar el botón si la extensión no es válida
            $("#uploadButton").hide();
            $("#output").text("Extensión de archivo no válida elige otro archivo");
        } else {
            // Mostrar el botón si la extensión es válida
            $("#uploadButton").show();
            $("#output").text(""); // Borrar cualquier mensaje anterior
        }
    });
});

// Función para verificar la extensión del archivo
function checkFileExtension(input) {
    var validExtensions = [".pdf", ".jpg", ".jpeg", ".png"];
    var fileName = input.value;
    var fileExtension = fileName.substring(fileName.lastIndexOf("."));

    return validExtensions.includes(fileExtension.toLowerCase());
}
