function toggleDiv(refId) {
    var refTypeSelect = document.getElementById("type_" + refId);
    var hiddenDiv = document.getElementById("hiddenDiv_" + refId);

    if (refTypeSelect.value == "1") {
        hiddenDiv.style.display = "none"; // Oculta el div
    } else {
        hiddenDiv.style.display = "block"; // Muestra el div
    }
}

document.addEventListener("DOMContentLoaded", function () {
    var refTypeSelects = document.querySelectorAll("[id^='type_']");
    refTypeSelects.forEach(function (select) {
        var refId = select.id.split("_")[1];
        select.addEventListener("change", function () {
            toggleDiv(refId);
        });
        toggleDiv(refId);
    });
});
