function toggle_icon(icon, type, new_type) {
    if (icon.hasClass(type)) {
        icon.removeClass(type).addClass(new_type);
    } else {
        icon.removeClass(new_type).addClass(type);
    }
}

function toggle_password(passwordInput) {
    if (passwordInput.attr("type") === "password") {
        passwordInput.attr("type", "text");
    } else {
        passwordInput.attr("type", "password");
    }
}

$("#togglePassword").click(function () {
    toggle_password($("#password"));
    toggle_icon($("#eye-icon-pass"), "bi-eye-fill", "bi-eye-slash-fill");
});

$("#togglePasswordConfirm").click(function () {
    toggle_password($("#password-confirmation"));
    toggle_icon($("#eye-icon-pass_confirm"), "bi-eye-fill", "bi-eye-slash-fill"); 
});