function setPests() {
    var input = '#pest-select'
    var pestClass = '.pest';
    var checkedPests = [];
    $(pestClass).each(function() {
        if ($(this).is(':checked')) {
            checkedPests.push(parseInt($(this).val()));
        }
    });
    $(input).val(JSON.stringify(checkedPests));
}

