function generate_password() {
    var date = new Date();
    var name = $("#name").val();
    var nickname = "@sp";
    var year = 0;

    if (name.trim() !== "") {
        var words = name.split(" ");
        for (var i = 0; i < words.length; i++) {
            var word = words[i];
            if (word.length > 0) {
                nickname += word[0].toLowerCase();
            }
        }
        year = date.getFullYear();
        year = year % 100;
        nickname += "" + year;
    }

    $("#password").val(nickname);
}

