var value = 0;

$(document).ready(function () {
    $("#curp").on("focusout keyup", function () {
        if (!validate_curp($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");

        }
    });

    $("#nss").on("focusout keyup", function () {
        if (!validate_nss($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");
        }
    });

    $("#rfc").on("focusout keyup", function () {
        if (!validate_rfc($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");
        }
    });

    $("#clabe").on("focusout keyup", function () {
        if (!validate_clabe($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");
        }
    });
    $("#cel").on("focusout keyup", function () {
        if (!validate_phone($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");
        }
    });
    $("#phone").on("focusout keyup", function () {
        if (!validate_phone($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");
        }
    });
  

    $("#company_phone").on("focusout keyup", function () {
        if (!validate_phone($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");
        }
    });
    $("#zip_code").on("focusout keyup", function () {
        if (!validate_cp($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");
        }
    });

    $("#email").on("focusout keyup", function () {
        if (!validate_email($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");
        }
    });
    $("#portal_email").on("focusout keyup", function () {  
        if (!validate_email($(this).val())) {
            $(this).addClass("text-danger");
        } else {
            $(this).removeClass("text-danger");
        }
    });
});

function validate_curp(curp) {
    var re = /^[A-Z]{4}\d{6}[HM]{1}[A-Z]{5}[A-Z0-9]{2}$/;
    if (re.test(curp)) {
        return true;
    } else {
        return false;
    }
}

function validate_nss(nss) {
    var nssRegex = /^[0-9]{11}$/;
    if (nss.match(nssRegex)) {
        return true;
    } else {
        return false;
    }
}

function validate_rfc(rfc) {
    var regexPersonaFisica = /^[A-Z]{4}\d{6}[A-Z0-9]{3}$/;
    var regexPersonaMoral = /^[A-Z]{3}\d{6}[A-Z0-9]{3}$/;

    if (regexPersonaFisica.test(rfc) || regexPersonaMoral.test(rfc)) {
        return true;
    } else {
        return false;
    }
}

function validate_clabe(clabe) {
    var numeroStr = clabe.toString();
    if (numeroStr.length === 18) {
        return true;
    } else {
        return false;
    }
}

function validate_phone(numero) {
    var patronTelefono = /^(?!01\s?800|800)(\d{10}|\d{7})$/;

    if (patronTelefono.test(numero)) {
        return true;
    } else {
        return false;
    }
}

function validate_cp(numero) {
    var patroncp = /^\d{5}$/;
    if (patroncp.test(numero)) {
        return true;
    } else {
        return false;
    }
}
function validate_email(correo) {
    var patronCorreo = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return patronCorreo.test(correo);
}

function validate_password(password) {
    if (password.length < 1) {
        return false;
    }
    if (!(/[a-z]/.test(password))) {
        return false;
    }
    if (!(/\d/.test(password))) {
        return false;
    }
    if (!(/[!@#$%^&*]/.test(password))) {
        return false;
    }
    return true; // Si pasa todas las verificaciones
}